        lastName: '',
        username: '',
        password: ''
      },
      submitted: false,
      termsStyleObject: {
        scrollTop: '0'
      }
    }
  },
  computed: {
    ...mapState('account', ['status'])
  },
  methods: {
    ...mapActions('account', ['register']),
    handleSubmit(e) {
      this.submitted = true;
      this.$validator.validate().then(valid => {
        if (valid) {
          this.register(this.user);
        }
      })
      },
      
      scrollPage () { 
        let scrollPercent =  document.getElementById('terms').scrollTop / document.getElementById('terms').scrollHeight *100;
